//same thing as home but class
import React from 'react';

class Rainbows extends React.Component {
    constructor (props) {
        super(props);
        this.state ={
            name: 'there'
        };
    }

    componentDidMount ( ){
        fetch('http://localhost:9000/users/1')
        .then(res => res.text())
        .then(res => {
            const user = JSON.parse(res);
            if (user) this.setState ({ name: user.firstName});
        })
        .catch(err => err);
        // Should be changed to dispatchEvent(getUser()) or something => action that goes to reducer and does the fetch call there
    }
}
