export const handleUser = (value) => {
  return {
    type: "HANDLE_USER",
    //add could be called payload or anything used in action
    value
  };
}

export const handleMsg = (value) => {
  return {
    type: "HANDLE_MSG",

    value
  };
}

export const handleSubmit = (name, msg) => {
  return {
    type: "HANDLE_SUBMIT",
    name,
    msg
  }
}
