//TODO EVERYTHING; template
import React, { Component, Fragment, useState } from 'react';
import {useSelector, useDispatch } from 'react-redux';
import {initialize} from "../actions"

export default function MsgList (){
    //state: {
    //     
    //}
    const dispatch = useDispatch();
    //const [messages, setMessages] = useState();
    const m_list = [];
    
    const listItems = useSelector(state => state.listItems);

    //callback function to dispatch the handleChange 'action' to our 'reducers'
    //todo: memoize with useCallback ??
    const initializeMessage = (m_list) => {
        dispatch(initialize); //debug this may need to be 3
     }

    


     //TODO: get action to populate ul with lis
      
    return( 
    <ul className= "msg_list" onLoad = {()=> {initializeMessage(m_list)}}>
        {listItems.map((msg, i) => {
            return (
                <Fragment key= {i}>
                    {
                            <li className="msg_send">
                            <div className="msg">
                            <div className ="msg_content">
                                <div className="msg_username"></div>
                                <div className="msg_message"></div>
                            </div>
                            </div>
                        </li>   
                    }
                </Fragment>
            )
        })}
        
    </ul>

    );

}
