import React from 'react';
import { connect } from 'react-redux';
import {increment} from '../actions';
import UserForm from './UserForm';


//TODO: CURRENTLY NOT USED; maybe delete later
class Button extends React.Component {
	render() {
        return (<div>
            <h1>The message is: {this.props.count}</h1>
            <button onClick={() => this.props.increment(5)}>
                Click Me!
            </button>
           {/*  <button  id= "decrement" onClick={() => this.props.decrement(5)}>
            Click Me!
            </button> */}
        </div>
            );
	}
}

//state has entire state of app!!
const mapStateToProps = (state) => { //name is by convention
    return{ count: state.count }; //now it will appear as props
}

export default connect(mapStateToProps, { increment }) (Button);
//calls a function that calls a function with Button as the param
