import { useState } from 'react';

const useModal = () => {
  const [isShowing, setIsShowing] = useState(false);

  const toggle = () => {
    setIsShowing(!isShowing);
  }

  const hide = () => {
    setIsShowing(false);
    toggle();
  }
  const show = () => {
    setIsShowing(true);
    toggle
  }
  

  return {
    isShowing,
    toggle,
    hide, 
    show
  }
};

export default useModal;
